import React from "react";
import { Form, Button } from "react-bootstrap";
import { useTransactionContext } from "./transaction-context";

const AddTransactionForm = ({
  newTransaction,
  handleInputChange,
  handleSubmit,
}) => {
  const { state } = useTransactionContext();

  return (
    <Form onSubmit={handleSubmit}>
      <Form.Group controlId="transactionDescription">
        <Form.Label>Transaction Description</Form.Label>
        <Form.Control
          type="text"
          name="description"
          value={newTransaction.description}
          onChange={handleInputChange}
          required
        />
      </Form.Group>

      <Form.Group controlId="transactionDate">
        <Form.Label>Transaction Date</Form.Label>
        <Form.Control
          type="date"
          name="transactionDate"
          value={newTransaction.transactionDate}
          onChange={handleInputChange}
          required
        />
      </Form.Group>

      <Form.Group controlId="transactionAmount">
        <Form.Label>Amount</Form.Label>
        <Form.Control
          type="number"
          step="0.01"
          name="amount"
          value={newTransaction.amount}
          onChange={handleInputChange}
          required
        />
      </Form.Group>

      <Form.Group controlId="transactionType">
        <Form.Label>Transaction Type</Form.Label>
        <Form.Control
          as="select"
          name="transactionType"
          value={newTransaction.transactionType}
          onChange={handleInputChange}
          required
        >
          <option value="">Select a type</option>
          <option value="DEPOSIT">Deposit</option>
          <option value="EXPENSE">Expense</option>
          <option value="TRANSFER">Transfer</option>
        </Form.Control>
      </Form.Group>

      <Form.Group controlId="recurring">
        <Form.Label>Recurring</Form.Label>
        <Form.Check
          type="checkbox"
          name="recurring"
          checked={newTransaction.recurring}
          onChange={handleInputChange}
        />
      </Form.Group>

      {newTransaction.recurring && (
        <Form.Group controlId="recurringFrequency">
          <Form.Label>Recurring Frequency</Form.Label>
          <Form.Control
            type="text"
            name="recurringFrequency"
            value={newTransaction.recurringFrequency}
            onChange={handleInputChange}
          />
        </Form.Group>
      )}

      {newTransaction.transactionType === "TRANSFER" && (
        <Form.Group controlId="relatedTransactionId">
          <Form.Label>Related Transaction ID</Form.Label>
          <Form.Control
            type="number"
            name="relatedTransactionId"
            value={newTransaction.relatedTransactionId}
            onChange={handleInputChange}
          />
        </Form.Group>
      )}

      <Button variant="primary" type="submit">
        Add Transaction
      </Button>
    </Form>
  );
};

export default AddTransactionForm;
