import axios from 'axios';

const API_BASE_URL = "http://localhost:8080";

export const getUserAccounts = (userId) => {
  return axios.get(`${API_BASE_URL}/users/${userId}/accounts`);
};

export const addAccount = (userId, account) => {
  return axios.post(`${API_BASE_URL}/accounts/add/${userId}`, account);
};

export const deleteAccount = (accountId) => {
  return axios.delete(`${API_BASE_URL}/accounts/delete/${accountId}`);
};
