import React from "react";
import { Button, Card, Col } from "react-bootstrap";

const TransactionCard = ({
  transaction,
  deleteTransaction,
  editTransaction,
}) => (
  <Col xs={12} sm={6} md={4} lg={3} className="mb-3">
    <Card style={{ width: "100%", padding: "10px" }}>
      <Card.Body>
        <Card.Title style={{ fontSize: "1rem" }}>
          {transaction.description}
        </Card.Title>
        <Card.Text as="div" style={{ fontSize: "0.9rem" }}>
          <div style={{ fontWeight: "bold" }}>
            Date: {transaction.transactionDate}
          </div>
          <div>Amount: ${transaction.amount}</div>
          <div>Type: {transaction.transactionType}</div>
          {transaction.recurring && (
            <div>Recurring: {transaction.recurring ? "Yes" : "No"}</div>
          )}
          {transaction.recurringFrequency && (
            <div>Frequency: {transaction.recurringFrequency}</div>
          )}
        </Card.Text>
        <div className="d-flex justify-content-center">
          <div className="d-flex" style={{ width: "100%" }}>
            <Button
              variant="primary"
              size="sm"
              onClick={() => editTransaction(transaction.transactionId)}
              className="me-2 flex-grow-1"
              style={{ flex: 1 }}
            >
              Edit
            </Button>
            <Button
              variant="danger"
              size="sm"
              onClick={() => deleteTransaction(transaction.transactionId)}
              className="flex-grow-1"
              style={{ flex: 1 }}
            >
              Delete
            </Button>
          </div>
        </div>
      </Card.Body>
    </Card>
  </Col>
);

export default TransactionCard;
