import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { useState } from "react";
import { registerUser } from "../api/api";
import { Modal, Button } from "react-bootstrap";
import { Link } from "react-router-dom";

export default function LoginForm() {
  // sets users data to to empty values
  const [userData, setUserData] = useState({
    email: "",
    password: "",
  });

  // sets the state of the modal
  const [modalShow, setModalShow] = useState(false);
  const [modalMessage, setModalMessage] = useState("");
  const [isSuccess, setIsSuccess] = useState(false);

  const handleChange = (event) => {
    const { name, value } = event.target;

    // updates user data when user enters it
    setUserData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setModalShow(false);
    setModalMessage("");

    try {
      const result = await registerUser(userData);
      setIsSuccess(true);
      setModalShow(true);
      //   setTimeout(() => (window.location.href = "/login"), 2000); // redirect to login after 2 seconds
    } catch (error) {
      setModalMessage("Login failed: " + error.message);
      setIsSuccess(false);
      setModalShow(true);
    }
  };

  return (
    <div className="vh-100 d-flex align-items-center justify-content-center">
      <div
        className="container d-flex flex-column align-items-center justify-content-center shadow bg-white rounded"
        style={{ width: 500, height: 600 }}
      >
        <form onSubmit={handleSubmit}>
          <div
            className="d-flex justify-content-center"
            style={{ marginTop: 30, marginBottom: 50 }}
          >
            <h1>Login</h1>
          </div>
          <div className="form-group">
            <label className="form-group">Email</label>
            <input
              type="email"
              className="form-control"
              name="email"
              value={userData.email}
              onChange={handleChange}
              style={{ width: 300 }}
              required
            ></input>
          </div>
          <div>
            <label className="form-group">Password</label>
            <input
              type="password"
              className="form-control"
              name="password"
              value={userData.password}
              onChange={handleChange}
              style={{ width: 300 }}
              required
            ></input>
          </div>
          <div
            className="d-flex justify-content-center"
            style={{ marginTop: 20 }}
          >
            <button
              type="submit"
              className="btn
        btn-primary btn-lg"
              style={{ width: 150 }}
            >
              Login
            </button>
          </div>
        </form>
        <div className="mt-20">
          <p>
            Don't have an account? <Link to="/register">Sign Up</Link>
          </p>
        </div>
      </div>
    </div>
  );
}
