import React from 'react';
import { Card, Button, Row, Col } from 'react-bootstrap';
import { useAccountContext } from './AccountContext';

const AccountList = () => {
  const { state, handleCloseAccount } = useAccountContext();

  return (
    <Row>
      {state.accounts.map(account => (
        <Col key={account.accountId} xs={12} sm={6} md={4} lg={3} className="mb-4">
          <Card>
            <Card.Body>
              <Card.Title>{account.accountName}</Card.Title>
              <Card.Text>
                <p style={{ fontWeight: 'bold' }}>Balance: ${account.balance}</p>
                <p>Type: {account.accountType}</p>
                <p>Credit Limit: ${account.creditLimit}</p>
              </Card.Text>
              <Button variant="danger" onClick={() => handleCloseAccount(account.accountId)}>Close Account</Button>
            </Card.Body>
          </Card>
        </Col>
      ))}
    </Row>
  );
};

export default AccountList;
