import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AccountNavBar from './account-nav-bar';

export default function AccountPage() {
    const [accounts, setAccounts] = useState([]);

    useEffect(() => {
        const userString = localStorage.getItem("user");
        if(userString) {
          const user = JSON.parse(userString);
          var userId = user.id;
        }
        axios.get(`http://localhost:8080/users/${userId}/accounts`)
        .then(response => setAccounts(response.data))
        .catch(error => console.error('There was an error fetching the accouints.', error));
    }, []);

    return (
      // <AccountNavBar />
      <div className="account-list">
      <h1>Account List</h1>
        <ul className="account-list__items">
          {accounts.map(account => (
            <li key={account.accountId} className="account-list__item">
              <div className="account-card">
                <div className="account-card__header">
                  <h3>{account.accountName}</h3>
                  <span className="account-card__balance">${account.balance}</span>
                </div>
                <div className="account-card__details">
                  {/* Display other account details with appropriate styling */}
                  <p>Type: {account.accountType}</p>
                  <p>Credit Limit: ${account.creditLimit}</p>
                  {/* ... other details */}
                  <p>Close Account</p>
                </div>
              </div>
            </li>
          ))}
        </ul>
      </div>
    );
}
