// AccountPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Card, Container, Row, Col } from 'react-bootstrap';
import UpdateAccountModal from './update-account-modal'; // Adjust the path as needed

const API_BASE_URL = "http://localhost:8080";

const AccountList = () => {
  const [accounts, setAccounts] = useState([]);
  const [showUpdateModal, setShowUpdateModal] = useState(false);
  const [selectedAccount, setSelectedAccount] = useState(null);

  useEffect(() => {
    fetchAccounts();
  }, []);

  const fetchAccounts = () => {
    const userString = localStorage.getItem("user");
    if (userString) {
      const user = JSON.parse(userString);
      var userId = user.id;
      axios.get(`${API_BASE_URL}/users/${userId}/accounts`)
        .then(response => {
          setAccounts(response.data);
        })
        .catch(error => {
          console.error('Error fetching accounts:', error);
        });
    }
  };

  const handleUpdateAccount = (updatedAccount) => {
    const updatedAccounts = accounts.map(account =>
      account.accountId === updatedAccount.accountId ? updatedAccount : account
    );
    setAccounts(updatedAccounts);
  };

  const handleEditAccount = (account) => {
    setSelectedAccount(account);
    setShowUpdateModal(true);
  };

  const handleCloseModal = () => {
    setShowUpdateModal(false);
    setSelectedAccount(null);
  };

  const handleDeleteAccount = (accountId) => {
    axios.delete(`${API_BASE_URL}/accounts/delete/${accountId}`)
      .then(response => {
        setAccounts(prevAccounts => prevAccounts.filter(account => account.accountId !== accountId));
        console.log(`Account with ID ${accountId} deleted.`);
      })
      .catch(error => {
        console.error('Error deleting account:', error);
      });
  };

  return (
    <Container>
      <h1>Account List</h1>
      <Row>
        {accounts.map(account => (
          <Col key={account.accountId} xs={12} sm={6} md={4} lg={3} className="mb-4">
            <Card>
              <Card.Body>
                <Card.Title>{account.accountName}</Card.Title>
                {/* <Card.Text> */}
                  <div style={{ fontWeight: 'bold' }}>Balance: ${account.balance}</div>
                  <div>Type: {account.accountType}</div>
                  <div>Credit Limit: ${account.creditLimit}</div>
                {/* </Card.Text> */}
                <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                  <Button variant="danger" onClick={() => handleDeleteAccount(account.accountId)}>Close Account</Button>
                  <Button variant="primary" onClick={() => handleEditAccount(account)}>Edit Account</Button>
                </div>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>

      {selectedAccount && (
        <UpdateAccountModal
          account={selectedAccount}
          show={showUpdateModal}
          onHide={handleCloseModal}
          onUpdate={handleUpdateAccount}
        />
      )}
    </Container>
  );
};

export default AccountList;
