import React, { useState } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import { deleteUser, updateUser } from "../api/api";

export default function Profile() {
  const userString = localStorage.getItem("user");
  const user = userString ? JSON.parse(userString) : null;

  const [modalShow, setModalShow] = useState(false);
  const [updateModalShow, setUpdateModalShow] = useState(false);
  const [modalMessage, setModalMessage] = useState("");
  const [isSuccess, setIsSuccess] = useState(false);
  const [updatedUser, setUpdatedUser] = useState(user);

  const labelContainerStyle = {
    display: "flex",
    alignItems: "center",
    width: "100%",
    marginTop: 40,
  };

  const buttonStyle = {
    width: 100,
    padding: "10px 20px",
  };

  const handleDelete = async () => {
    try {
      await deleteUser(user);
      setModalMessage("User deleted successfully!");
      setIsSuccess(true);
      setModalShow(true);
      setTimeout(() => (window.location.href = "/register"), 2000);
    } catch (error) {
      setModalMessage("Delete failed: " + error.message);
      setIsSuccess(false);
      setModalShow(true);
    }
  };

  const handleUpdate = async () => {
    try {
      await updateUser(updatedUser);
      setModalMessage("User updated successfully!");
      setIsSuccess(true);
      setModalShow(true);
      setUpdateModalShow(false);
      localStorage.setItem("user", JSON.stringify(updatedUser));
    } catch (error) {
      setModalMessage("Update failed: " + error.message);
      setIsSuccess(false);
      setModalShow(true);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUpdatedUser((prevUser) => ({
      ...prevUser,
      [name]: value,
    }));
  };

  return (
    <>
      <div className="vh-100 d-flex align-items-center justify-content-center">
        <div
          className="container d-flex flex-column align-items-center justify-content-start shadow bg-white rounded"
          style={{ width: 500, height: 600 }}
        >
          <h1 className="mt-4">Profile</h1>
          <div className="d-flex flex-column align-items-start w-100 p-4">
            <div style={labelContainerStyle}>
              <label className="me-2" style={{ fontWeight: "bold" }}>
                Name:
              </label>
              <p className="mb-0">{user?.firstName}</p>
            </div>
            <div style={labelContainerStyle}>
              <label className="me-2" style={{ fontWeight: "bold" }}>
                Last Name:
              </label>
              <p className="mb-0">{user?.lastName}</p>
            </div>
            <div style={labelContainerStyle}>
              <label className="me-2" style={{ fontWeight: "bold" }}>
                Email:
              </label>
              <p className="mb-0">{user?.email}</p>
            </div>
            <div style={labelContainerStyle}>
              <label className="me-2" style={{ fontWeight: "bold" }}>
                Password:
              </label>
              <p className="mb-0">{user?.password}</p>
            </div>
          </div>
          <div className="d-flex justify-content-between w-50 mt-5">
            <button
              className="btn btn-primary"
              style={buttonStyle}
              onClick={() => setUpdateModalShow(true)}
            >
              EDIT
            </button>
            <button
              onClick={handleDelete}
              className="btn btn-danger"
              style={buttonStyle}
            >
              DELETE
            </button>
          </div>
        </div>
        <Modal show={modalShow} onHide={() => setModalShow(false)}>
          <Modal.Header closeButton>
            <Modal.Title>{isSuccess ? "Success" : "Error"}</Modal.Title>
          </Modal.Header>
          <Modal.Body>{modalMessage}</Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => setModalShow(false)}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>

        <Modal show={updateModalShow} onHide={() => setUpdateModalShow(false)}>
          <Modal.Header closeButton>
            <Modal.Title>Update Profile</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form>
              <Form.Group controlId="formFirstName">
                <Form.Label>First Name</Form.Label>
                <Form.Control
                  required
                  type="text"
                  name="firstName"
                  value={updatedUser.firstName}
                  onChange={handleChange}
                />
              </Form.Group>
              <Form.Group controlId="formLastName" className="mt-3">
                <Form.Label>Last Name</Form.Label>
                <Form.Control
                  type="text"
                  name="lastName"
                  value={updatedUser.lastName}
                  onChange={handleChange}
                />
              </Form.Group>
              <Form.Group controlId="formEmail" className="mt-3">
                <Form.Label>Email</Form.Label>
                <Form.Control
                  type="email"
                  name="email"
                  value={updatedUser.email}
                  onChange={handleChange}
                />
              </Form.Group>
              <Form.Group controlId="formPassword" className="mt-3">
                <Form.Label>Password</Form.Label>
                <Form.Control
                  type="password"
                  name="password"
                  value={updatedUser.password}
                  onChange={handleChange}
                />
              </Form.Group>
            </Form>
          </Modal.Body>
          <Modal.Footer>
            <Button
              variant="secondary"
              onClick={() => setUpdateModalShow(false)}
            >
              Cancel
            </Button>
            <Button variant="primary" onClick={handleUpdate}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
      </div>
    </>
  );
}
