import axios from "axios";

const API_BASE_URL = "http://localhost:8080";

export const fetchTransactionsApi = async (accountId) => {
  const response = await axios.get(
    `${API_BASE_URL}/transactions/account/${accountId}`
  );
  return response.data;
};

export const addTransactionApi = async (accountId, newTransaction) => {
  const response = await axios.post(
    `${API_BASE_URL}/transactions/${accountId}`,
    newTransaction
  );
  return response.data;
};

export const deleteTransactionApi = async (transactionId) => {
  const response = await axios.delete(
    `${API_BASE_URL}/transactions/${transactionId}`
  );
  return response.data;
};

export const fetchUserAccountsApi = async (userId) => {
  const response = await axios.get(`${API_BASE_URL}/users/${userId}/accounts`);
  return response.data;
};

export const fetchAccountDetailsApi = async (accountId) => {
  const response = await axios.get(`${API_BASE_URL}/accounts/${accountId}`);
  return response.data;
};
