import React from 'react';
import { Form, Button } from 'react-bootstrap';

const AddTransactionForm = ({ newTransaction, handleInputChange, handleSubmit }) => (
  <Form onSubmit={handleSubmit}>
    <Form.Group>
      <Form.Label>Transaction Description</Form.Label>
      <Form.Control type='text' name='description' value={newTransaction.description} onChange={handleInputChange} required />
    </Form.Group>
    <Form.Group>
      <Form.Label>Transaction Type</Form.Label>
      <Form.Control as='select' name='TransactionType' value={newTransaction.transactionType} onChange={handleInputChange} required >
        <option value="DEPOSIT">DEPOSIT</option>
        <option value="EXPENSE">EXPENSE</option>
        {/*
        <option value="TRANSFER">TRANSFER</option>
        /*/}
      </Form.Control>
    </Form.Group>

    {newTransaction.transactionType === 'DEPOSIT' && (
      <>
        <Form.Group>
          <Form.Label>Amount</Form.Label>
          <Form.Control type='number' name='amount' value={newTransaction.amount} onChange={handleInputChange} required />
        </Form.Group>
      </>
    )}

    {newTransaction.transactionType === 'EXPENSE' && (
      <>
         <Form.Group>
          <Form.Label>Amount</Form.Label>
          <Form.Control type='number' name='amount' value={newTransaction.amount} onChange={handleInputChange} required />
        </Form.Group>
      </>
    )}

    <Button type='submit'>Add Transaction</Button>
  </Form>
);

export default AddTransactionForm;
