import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Card, Form, Container, Row, Col } from 'react-bootstrap';
import './account-page.css';

const API_BASE_URL = "http://localhost:8080";

function closeAccount(accountId, setState) {
  axios.delete(`${API_BASE_URL}/accounts/delete/${accountId}`)
    .then(response => {
      if (response.data) {
        setState(prevState => ({
          ...prevState,
          accounts: prevState.accounts.filter(account => account.accountId !== accountId)
        }));
        console.log(`Account with ID ${accountId} deleted.`);
      }
    })
    .catch(error => {
      console.error('There was an error deleting the account.', error);
    });
}

export default function AccountPage() {
  const [state, setState] = useState({
    accounts: [],
    newAccount: {
      accountName: '',
      balance: '',
      accountType: 'CREDIT',
      creditLimit: '',
      apr: '',
      principal: '',
      loanDisbursementDate: '',
      loanRepaymentDate: '',
      minMonthlyPayment: ''
    }
  });

  useEffect(() => {
    const userString = localStorage.getItem("user");
    if (userString) {
      const user = JSON.parse(userString);
      var userId = user.id;
    }
    axios.get(`${API_BASE_URL}/users/${userId}/accounts`)
      .then(response => {
        setState(prevState => ({
          ...prevState,
          accounts: response.data
        }));
      })
      .catch(error => console.error('There was an error fetching the accounts.', error));
  }, []);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setState(prevState => ({
      ...prevState,
      newAccount: {
        ...prevState.newAccount,
        [name]: value
      }
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const userString = localStorage.getItem("user");
    if (userString) {
      const user = JSON.parse(userString);
      var userId = user.id;
    }

    axios.post(`${API_BASE_URL}/accounts/add/${userId}`, state.newAccount)
      .then(response => {
        setState(prevState => ({
          ...prevState,
          accounts: [...prevState.accounts, response.data],
          newAccount: {
            accountName: '',
            balance: '',
            accountType: 'CREDIT',
            creditLimit: '',
            apr: '',
            principal: '',
            loanDisbursementDate: '',
            loanRepaymentDate: '',
            minMonthlyPayment: ''
          }
        }));
      })
      .catch(error => {
        console.error('There was an error creating the account.', error);
      });
  };

  return (
    <Container> // cons
      {/* <div className="account-list"> */}
        <h1>Account List</h1>
        <Row>
          {state.accounts.map(account => (
            <Col key={account.accountId} xs={12} sm={6} md={4} lg={3}>
              <Card className="account-card">
                <Card.Body>
                  <Card.Title>{account.accountName}</Card.Title>
                  <Card.Text>
                    <span className="account-card__balance">Balance: ${account.balance}</span>
                    <p>Type: {account.accountType}</p>
                    <p>Credit Limit: ${account.creditLimit}</p>
                    {/* ... other details */}
                  </Card.Text>
                  <Button variant="danger" onClick={() => closeAccount(account.accountId, setState)}>Close Account</Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      {/* </div> */}

      <h2>Add New Account</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Group>
          <Form.Label>Account Name</Form.Label>
          <Form.Control type='text' name='accountName' value={state.newAccount.accountName} onChange={handleInputChange} required />
        </Form.Group>

        <Form.Group>
          <Form.Label>Balance</Form.Label>
          <Form.Control type='number' name='balance' value={state.newAccount.balance} onChange={handleInputChange} required />
        </Form.Group>

        <Form.Group>
          <Form.Label>Account Type</Form.Label>
          <Form.Control as='select' name='accountType' value={state.newAccount.accountType} onChange={handleInputChange} required >
            <option value="CREDIT">CREDIT</option>
            <option value="DEBIT">DEBIT</option>
            <option value="LOAN">LOAN</option>
          </Form.Control>
        </Form.Group>

        {state.newAccount.accountType === 'CREDIT' && (
          <>
            <Form.Group>
              <Form.Label>Credit Limit</Form.Label>
              <Form.Control type="number" name="creditLimit" value={state.newAccount.creditLimit} onChange={handleInputChange} />
            </Form.Group>

            <Form.Group>
              <Form.Label>APR</Form.Label>
              <Form.Control type="number" name="apr" value={state.newAccount.apr} onChange={handleInputChange} />
            </Form.Group>
          </>
        )}

        {state.newAccount.accountType === 'LOAN' && (
          <>
            <Form.Group>
              <Form.Label>Principal</Form.Label>
              <Form.Control type="number" name="principal" value={state.newAccount.principal} onChange={handleInputChange} />
            </Form.Group>

            <Form.Group>
              <Form.Label>Loan Disbursement Date</Form.Label>
              <Form.Control type="date" name="loanDisbursementDate" value={state.newAccount.loanDisbursementDate} onChange={handleInputChange} />
            </Form.Group>

            <Form.Group>
              <Form.Label>Loan Repayment Date</Form.Label>
              <Form.Control type="date" name="loanRepaymentDate" value={state.newAccount.loanRepaymentDate} onChange={handleInputChange} />
            </Form.Group>

            <Form.Group>
              <Form.Label>Minimum Monthly Payment</Form.Label>
              <Form.Control type="number" name="minMonthlyPayment" value={state.newAccount.minMonthlyPayment} onChange={handleInputChange} />
            </Form.Group>
          </>
        )}

        <Button type='submit'>Add Account</Button>
      </Form>
    </Container>
  );
}
