// src/components/TransactionComponent/transaction-page.js
import React, { useContext, useState, useEffect } from "react";
import { Container, Row, Button, Form, Card } from "react-bootstrap";
import { TransactionContext } from "./transaction-context";
import TransactionList from "./transaction-list";
import AddTransactionForm from "./add-transaction-form";

const TransactionPage = () => {
  const {
    state,
    fetchTransactions,
    handleInputChange,
    handleSubmit,
    fetchUserAccounts,
    dispatch,
  } = useContext(TransactionContext);
  const [showModal, setShowModal] = useState(false);
  const [selectedAccount, setSelectedAccount] = useState(null);

  useEffect(() => {
    const userString = localStorage.getItem("user");
    if (userString) {
      const user = JSON.parse(userString);
      console.log("Fetching user accounts for user ID:", user.id); // Debug log
      fetchUserAccounts(user.id);
    }
  }, [fetchUserAccounts]);

  const handleShow = () => setShowModal(true);
  const handleClose = () => setShowModal(false);

  const handleAccountChange = async (event) => {
    const accountId = event.target.value;
    console.log("Selected account ID:", accountId); // Debug log
    if (accountId) {
      const selectedAccount = state.accounts.find(
        (account) => account.accountId === parseInt(accountId)
      );
      if (selectedAccount) {
        setSelectedAccount(selectedAccount);
        await fetchTransactions(accountId);
      }
    }
  };

  useEffect(() => {
    console.log("State updated:", state); // Debug log
  }, [state]);

  return (
    <Container style={{ marginTop: "80px" }}>
      <h1>Transaction Management</h1>
      <Form.Group controlId="accountSelect">
        <Form.Label>Select Account</Form.Label>
        <Form.Control as="select" onChange={handleAccountChange}>
          <option value="">Select an account</option>
          {Array.isArray(state.accounts) &&
            state.accounts.map((account) => (
              <option key={account.accountId} value={account.accountId}>
                {account.accountName}
              </option>
            ))}
        </Form.Control>
      </Form.Group>
      {selectedAccount && (
        <>
          <h2>Account Details</h2>
          <Card className="mb-4">
            <Card.Body>
              <Card.Title>{selectedAccount.accountName}</Card.Title>
              <Card.Text>
                {selectedAccount.balance && (
                  <p>Balance: {selectedAccount.balance}</p>
                )}
                {selectedAccount.apr && <p>APR: {selectedAccount.apr}</p>}
                {selectedAccount.type && <p>Type: {selectedAccount.type}</p>}
                {selectedAccount.creditLimit && (
                  <p>Credit Limit: {selectedAccount.creditLimit}</p>
                )}
              </Card.Text>
            </Card.Body>
          </Card>
          <h3>Transactions</h3>
          <TransactionList transactions={state.transactions} />
        </>
      )}
      <Button variant="primary" onClick={handleShow}>
        Add New Transaction
      </Button>
      <AddTransactionForm
        show={showModal}
        handleClose={handleClose}
        handleSubmit={handleSubmit}
        handleInputChange={handleInputChange}
        newTransaction={state.newTransaction}
      />
    </Container>
  );
};

export default TransactionPage;
