import React, { createContext, useContext, useState, useCallback } from "react";
import {
  fetchTransactionsApi,
  addTransactionApi,
  deleteTransactionApi,
  fetchUserAccountsApi,
  fetchAccountDetailsApi,
} from "./transaction-api";

export const TransactionContext = createContext();

export const TransactionProvider = ({ children }) => {
  const [state, setState] = useState({
    accounts: [],
    selectedAccount: null,
    accountDetails: {},
    transactions: [],
    newTransaction: {
      transactionDate: "",
      amount: "",
      description: "",
      recurring: "",
      recurringFrequency: "",
      transactionType: "",
      relatedTransactionId: "",
    },
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setState((prevState) => ({
      ...prevState,
      newTransaction: {
        ...prevState.newTransaction,
        [name]: value,
      },
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const accountString = localStorage.getItem("account");
    if (accountString) {
      const account = JSON.parse(accountString);
      var accountId = account.id;
    }

    addTransactionApi(accountId, state.newTransaction)
      .then((response) => {
        setState((prevState) => ({
          ...prevState,
          transactions: [...prevState.transactions, response.data],
          newTransaction: {
            transactionDate: "",
            amount: "",
            description: "",
            recurring: "",
            recurringFrequency: "",
            transactionType: "",
            relatedTransactionId: "",
          },
        }));
      })
      .catch((error) => {
        console.error("There was an error creating the transaction.", error);
      });
  };

  const fetchUserAccounts = useCallback((userId) => {
    fetchUserAccountsApi(userId)
      .then((response) => {
        setState((prevState) => ({
          ...prevState,
          accounts: response,
        }));
      })
      .catch((error) =>
        console.error("There was an error fetching the accounts.", error)
      );
  }, []);

  const fetchTransactions = useCallback((accountId) => {
    fetchTransactionsApi(accountId)
      .then((response) => {
        setState((prevState) => ({
          ...prevState,
          transactions: response,
        }));
      })
      .catch((error) =>
        console.error("There was an error fetching the transactions.", error)
      );
  }, []);

  const handleAccountChange = (event) => {
    const accountId = event.target.value;
    setState((prevState) => ({
      ...prevState,
      selectedAccount: accountId,
    }));
    fetchAccountDetailsApi(accountId)
      .then((response) => {
        setState((prevState) => ({
          ...prevState,
          accountDetails: response,
        }));
      })
      .catch((error) =>
        console.error("There was an error fetching the account details.", error)
      );
  };

  const deleteTransaction = (transactionId) => {
    deleteTransactionApi(transactionId)
      .then((response) => {
        if (response.data) {
          setState((prevState) => ({
            ...prevState,
            transactions: prevState.transactions.filter(
              (transaction) => transaction.transactionId !== transactionId
            ),
          }));
          console.log(`Transaction with ID ${transactionId} deleted.`);
        }
      })
      .catch((error) => {
        console.error("There was an error deleting the transaction.", error);
      });
  };

  return (
    <TransactionContext.Provider
      value={{
        state,
        fetchUserAccounts,
        fetchTransactions,
        deleteTransaction,
        handleSubmit,
        handleInputChange,
        handleAccountChange,
      }}
    >
      {children}
    </TransactionContext.Provider>
  );
};

export const useTransactionContext = () => {
  return useContext(TransactionContext);
};
