import React from 'react';
import { Col, Card, Button } from 'react-bootstrap';
import { useTransactionContext } from './transaction-context';

const TransactionList = ({ transactions }) => {
  const { deleteTransaction } = useTransactionContext();

  return (
    <>
      {transactions.map(transaction => (
        <Col key={transaction.transactionId} xs={12} sm={6} md={4} lg={3} className="mb-4">
          <Card>
            <Card.Body>
              <Card.Title>{transaction.description}</Card.Title>
              <Card.Text as="div">
                <div style={{ fontWeight: 'bold' }}>Date: ${transaction.transactionDate}</div>
                <div>Amount: ${transaction.amount}</div>
                <div>Type: {transaction.transactionType}</div>
                {/* ... other details */}
              </Card.Text>
              <div className="d-flex justify-content-between m-2">
                <Button variant="danger" onClick={() => deleteTransaction(transaction.transactionId)}>Delete Transaction</Button>
              </div>
            </Card.Body>
          </Card>
        </Col>
      ))}
    </>
  );
};


export default TransactionList;
