import React, { useContext, useEffect } from "react";
import { Container, Row, Form, Card } from "react-bootstrap";
import { TransactionContext } from "./transaction-context";
import TransactionList from "./transaction-list";
import AddTransactionForm from "./add-transaction-form";
import "./transaction-page.css";

export default function TransactionPage() {
  const {
    state,
    fetchUserAccounts,
    handleInputChange,
    handleSubmit,
    handleAccountChange,
  } = useContext(TransactionContext);

  useEffect(() => {
    const userString = localStorage.getItem("user");
    if (userString) {
      const user = JSON.parse(userString);
      fetchUserAccounts(user.id);
    }
  }, [fetchUserAccounts]);

  const renderAccountDetail = (label, value) => {
    return value ? (
      <div>
        <strong>{label}:</strong> {value}
      </div>
    ) : null;
  };

  return (
    <Container>
      <h1>Transactions</h1>
      <Form.Group controlId="selectAccount">
        <Form.Label>Select Account</Form.Label>
        <Form.Control as="select" onChange={handleAccountChange}>
          <option value="">Select an account</option>
          {state.accounts.map((account) => (
            <option key={account.accountId} value={account.accountId}>
              {account.accountName}
            </option>
          ))}
        </Form.Control>
      </Form.Group>
      {state.selectedAccount && (
        <>
          <h2>Account Details</h2>
          <Card>
            <Card.Body>
              <Card.Title>{state.accountDetails.accountName}</Card.Title>
              <Card.Text as="div">
                {renderAccountDetail("Balance", state.accountDetails.balance)}
                {renderAccountDetail("APR", state.accountDetails.apr)}
                {renderAccountDetail("Type", state.accountDetails.accountType)}
                {renderAccountDetail(
                  "Credit Limit",
                  state.accountDetails.creditLimit
                )}
                {renderAccountDetail(
                  "Loan Disbursement Date",
                  state.accountDetails.loanDisbursementDate
                )}
                {renderAccountDetail(
                  "Loan Repayment Date",
                  state.accountDetails.loanRepaymentDate
                )}
                {renderAccountDetail(
                  "Minimum Monthly Payment",
                  state.accountDetails.minMonthlyPayment
                )}
                {renderAccountDetail(
                  "Principal",
                  state.accountDetails.principal
                )}
              </Card.Text>
            </Card.Body>
          </Card>
        </>
      )}
      {state.transactions.length > 0 ? (
        <Row>
          <TransactionList transactions={state.transactions} />
        </Row>
      ) : (
        <p>No transactions found.</p>
      )}
      <h2>Add New Transaction</h2>
      <AddTransactionForm
        newTransaction={state.newTransaction}
        handleInputChange={handleInputChange}
        handleSubmit={handleSubmit}
      />
    </Container>
  );
}
