// account-page.js
import React, { useContext, useEffect } from 'react';
import { Container, Row } from 'react-bootstrap';
import { AccountContext } from './account-context';
import AccountList from './account-list';
import AddAccountForm from './add-account-form';

export default function AccountPage() {
  const { state, fetchAccounts, handleInputChange, handleSubmit } = useContext(AccountContext);

  useEffect(() => {
    const userString = localStorage.getItem("user");
    if (userString) {
      const user = JSON.parse(userString);
      fetchAccounts(user.id);
    }
  }, [fetchAccounts]);

  const handleAccountUpdate = (updatedAccount) => {
    setState(prevState => ({
      ...prevState,
      accounts: prevState.accounts.map(account =>
        account.accountId === updatedAccount.accountId ? updatedAccount : account
      )
    }));
  };

  return (
    <Container>
      <h1>Account List</h1>
      <Row>
        <AccountList accounts={state.accounts} onAccountUpdate={handleAccountUpdate} />
      </Row>
      <h2>Add New Account</h2>
      <AddAccountForm
        newAccount={state.newAccount}
        handleInputChange={handleInputChange}
        handleSubmit={handleSubmit}
      />
    </Container>
  );
}
