import axios from "axios";

const API_BASE_URL = "http://localhost:8080";

export const fetchTransactionsApi = (accountId) => {
  console.log(`Calling fetchTransactionsApi with accountId: ${accountId}`);
  return axios
    .get(`${API_BASE_URL}/transactions/account/${accountId}`)
    .then((response) => {
      console.log("Fetched transactions:", response.data);
      return response;
    })
    .catch((error) => {
      console.error("Error fetching transactions:", error);
      throw error;
    });
};

export const addTransactionApi = (accountId, newTransaction) => {
  console.log(
    `Calling addTransactionApi with accountId: ${accountId}`,
    newTransaction
  );
  return axios
    .post(`${API_BASE_URL}/transactions/${accountId}`, newTransaction)
    .then((response) => {
      console.log("Transaction added:", response.data);
      return response;
    })
    .catch((error) => {
      console.error("Error adding transaction:", error);
      throw error;
    });
};

export const deleteTransactionApi = (transactionId) => {
  console.log(
    `Calling deleteTransactionApi with transactionId: ${transactionId}`
  );
  return axios
    .delete(`${API_BASE_URL}/transactions/${transactionId}`)
    .then((response) => {
      console.log("Transaction deleted:", response.data);
      return response;
    })
    .catch((error) => {
      console.error("Error deleting transaction:", error);
      throw error;
    });
};

export const fetchUserAccountsApi = (userId) => {
  console.log(`Calling fetchUserAccountsApi with userId: ${userId}`);
  return axios
    .get(`${API_BASE_URL}/users/${userId}/accounts`)
    .then((response) => {
      console.log("Fetched user accounts:", response.data);
      return response;
    })
    .catch((error) => {
      console.error("Error fetching user accounts:", error);
      throw error;
    });
};
