import React, { createContext, useContext, useState, useCallback } from "react";
import axios from "axios";

export const TransactionContext = createContext();

export const TransactionProvider = ({ children }) => {
  const [state, setState] = useState({
    accounts: [],
    transactions: [],
    newTransaction: {
      transactionDate: "",
      amount: "",
      description: "",
      recurring: "",
      recurringFrequency: "",
      transactionType: "",
      relatedTransactionId: "",
    },
  });

  const fetchUserAccounts = useCallback(async (userId) => {
    try {
      const response = await axios.get(
        `http://localhost:8080/users/${userId}/accounts`
      );
      setState((prevState) => ({
        ...prevState,
        accounts: response.data,
      }));
    } catch (error) {
      console.error("There was an error fetching the accounts.", error);
    }
  }, []);

  const fetchTransactions = useCallback(async (accountId) => {
    try {
      const response = await axios.get(
        `http://localhost:8080/transactions/account/${accountId}`
      );
      setState((prevState) => ({
        ...prevState,
        transactions: response.data,
      }));
    } catch (error) {
      console.error("There was an error fetching the transactions.", error);
    }
  }, []);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setState((prevState) => ({
      ...prevState,
      newTransaction: {
        ...prevState.newTransaction,
        [name]: value,
      },
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const accountString = localStorage.getItem("account");
      const account = JSON.parse(accountString);
      const response = await axios.post(
        `http://localhost:8080/transactions/${account.accountId}`,
        state.newTransaction
      );
      setState((prevState) => ({
        ...prevState,
        transactions: [...prevState.transactions, response.data],
        newTransaction: {
          transactionDate: "",
          amount: "",
          description: "",
          recurring: "",
          recurringFrequency: "",
          transactionType: "",
          relatedTransactionId: "",
        },
      }));
    } catch (error) {
      console.error("There was an error creating the transaction.", error);
    }
  };

  return (
    <TransactionContext.Provider
      value={{
        state,
        fetchUserAccounts,
        fetchTransactions,
        handleInputChange,
        handleSubmit,
      }}
    >
      {children}
    </TransactionContext.Provider>
  );
};

export const useTransactionContext = () => {
  return useContext(TransactionContext);
};
