// src/components/TransactionComponent/transaction-card.js
import React from "react";
import { Button, Card, Col } from "react-bootstrap";

const TransactionCard = ({ transaction, deleteTransaction }) => (
  <Col xs={12} sm={6} md={4} lg={3} className="mb-4">
    <Card>
      <Card.Body>
        <Card.Title>{transaction.description}</Card.Title>
        <Card.Text as="div">
          <div style={{ fontWeight: "bold" }}>
            Date: {transaction.transactionDate}
          </div>
          <div>Amount: ${transaction.amount}</div>
          <div>Type: {transaction.transactionType}</div>
          {transaction.recurring && (
            <div>Recurring: {transaction.recurring ? "Yes" : "No"}</div>
          )}
          {transaction.recurringFrequency && (
            <div>Frequency: {transaction.recurringFrequency}</div>
          )}
          {transaction.relatedTransactionId && (
            <div>Related Transaction: {transaction.relatedTransactionId}</div>
          )}
        </Card.Text>
        <Button
          variant="danger"
          onClick={() => deleteTransaction(transaction.transactionId)}
        >
          Delete Transaction
        </Button>
      </Card.Body>
    </Card>
  </Col>
);

export default TransactionCard;
