// src/components/TransactionComponent/transaction-context.js
import React, { createContext, useReducer, useCallback } from "react";
import {
  fetchTransactionsApi,
  fetchUserAccountsApi,
  addTransactionApi,
} from "../../api/transaction-api";

const initialState = {
  accounts: [],
  selectedAccount: null,
  transactions: [],
  newTransaction: {
    transactionDate: "",
    amount: "",
    description: "",
    recurring: false,
    recurringFrequency: "",
    transactionType: "",
    relatedTransactionId: "",
  },
};

const TransactionContext = createContext(initialState);

const transactionReducer = (state, action) => {
  switch (action.type) {
    case "SET_ACCOUNTS":
      return { ...state, accounts: action.payload };
    case "SET_SELECTED_ACCOUNT":
      return { ...state, selectedAccount: action.payload };
    case "SET_TRANSACTIONS":
      return { ...state, transactions: action.payload };
    case "SET_NEW_TRANSACTION":
      return { ...state, newTransaction: action.payload };
    default:
      return state;
  }
};

const TransactionProvider = ({ children }) => {
  const [state, dispatch] = useReducer(transactionReducer, initialState);

  const fetchUserAccounts = useCallback(async (userId) => {
    const accounts = await fetchUserAccountsApi(userId);
    dispatch({ type: "SET_ACCOUNTS", payload: accounts });
  }, []);

  const fetchTransactions = useCallback(async (accountId) => {
    const transactions = await fetchTransactionsApi(accountId);
    dispatch({ type: "SET_TRANSACTIONS", payload: transactions });
  }, []);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    dispatch({
      type: "SET_NEW_TRANSACTION",
      payload: { ...state.newTransaction, [name]: value },
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    await addTransactionApi(
      state.selectedAccount.accountId,
      state.newTransaction
    );
    fetchTransactions(state.selectedAccount.accountId);
  };

  return (
    <TransactionContext.Provider
      value={{
        state,
        fetchUserAccounts,
        fetchTransactions,
        handleInputChange,
        handleSubmit,
      }}
    >
      {children}
    </TransactionContext.Provider>
  );
};

export { TransactionContext, TransactionProvider };
