import React from "react";
import { Card, Button } from "react-bootstrap";

const TransactionCard = ({
  transaction,
  deleteTransaction,
  editTransaction,
}) => {
  const handleDelete = () => {
    deleteTransaction(transaction.transactionId);
  };

  const handleEdit = () => {
    editTransaction(transaction);
  };

  return (
    <Card className="mb-3">
      <Card.Body>
        <Card.Title>{transaction.description}</Card.Title>
        <Card.Subtitle className="mb-2 text-muted">
          {transaction.transactionType}
        </Card.Subtitle>
        <Card.Text>
          Amount: {transaction.amount} <br />
          Date: {transaction.transactionDate} <br />
          Recurring: {transaction.recurring ? "Yes" : "No"} <br />
          {transaction.recurring && (
            <>Recurring Frequency: {transaction.recurringFrequency}</>
          )}
        </Card.Text>
        <Button variant="primary" onClick={handleEdit} className="me-2">
          Edit
        </Button>
        <Button variant="danger" onClick={handleDelete}>
          Delete
        </Button>
      </Card.Body>
    </Card>
  );
};

export default TransactionCard;
