import React, { useContext, useEffect, useState } from "react";
import { Container, Row, Form, Card, Button, Modal } from "react-bootstrap";
import { TransactionContext } from "./transaction-context";
import TransactionList from "./transaction-list";
import AddTransactionForm from "./add-transaction-form";

export default function TransactionPage() {
  const { state, handleAccountChange, handleInputChange, handleSubmit } =
    useContext(TransactionContext);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    const userString = localStorage.getItem("user");
    if (userString) {
      const user = JSON.parse(userString);
      fetchUserAccounts(user.userId);
    }
  }, [fetchUserAccounts]);

  const handleCloseModal = () => setShowModal(false);
  const handleShowModal = () => setShowModal(true);

  return (
    <Container>
      <h1>Transaction List</h1>
      <Form.Group controlId="selectAccount">
        <Form.Label>Select Account</Form.Label>
        <Form.Control as="select" onChange={handleAccountChange}>
          <option value="">Select an account</option>
          {state.accounts.map((account) => (
            <option key={account.accountId} value={account.accountId}>
              {account.accountName}
            </option>
          ))}
        </Form.Control>
      </Form.Group>
      {state.selectedAccount && (
        <div>
          <h2>Account Details</h2>
          <Card>
            <Card.Body>
              <Card.Title>{state.selectedAccount.accountName}</Card.Title>
              {state.selectedAccount.balance && (
                <Card.Text>Balance: {state.selectedAccount.balance}</Card.Text>
              )}
              {state.selectedAccount.apr && (
                <Card.Text>APR: {state.selectedAccount.apr}</Card.Text>
              )}
              {state.selectedAccount.type && (
                <Card.Text>Type: {state.selectedAccount.type}</Card.Text>
              )}
              {state.selectedAccount.creditLimit && (
                <Card.Text>
                  Credit Limit: {state.selectedAccount.creditLimit}
                </Card.Text>
              )}
            </Card.Body>
          </Card>
        </div>
      )}
      <Row>
        <TransactionList transactions={state.transactions} />
      </Row>
      <Button variant="primary" onClick={handleShowModal}>
        Add New Transaction
      </Button>

      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Add New Transaction</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <AddTransactionForm
            newTransaction={state.newTransaction}
            handleInputChange={handleInputChange}
            handleSubmit={(e) => {
              handleSubmit(e);
              handleCloseModal();
            }}
          />
        </Modal.Body>
      </Modal>
    </Container>
  );
}
