const API_BASE_URL = "http://localhost:8080";

export const fetchTransactionsApi = async (accountId) => {
  const response = await fetch(
    `${API_BASE_URL}/transactions/account/${accountId}`
  );
  if (!response.ok) {
    throw new Error("Network response was not ok");
  }
  return response.json();
};

export const addTransactionApi = async (accountId, newTransaction) => {
  const response = await fetch(`${API_BASE_URL}/transactions/${accountId}`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(newTransaction),
  });
  if (!response.ok) {
    throw new Error("Network response was not ok");
  }
  return response.json();
};

export const deleteTransactionApi = async (transactionId) => {
  const response = await fetch(
    `${API_BASE_URL}/transactions/${transactionId}`,
    {
      method: "DELETE",
    }
  );
  if (!response.ok) {
    throw new Error("Network response was not ok");
  }
  return response.json();
};

export const fetchUserAccountsApi = async (userId) => {
  const response = await fetch(`${API_BASE_URL}/accounts/user/${userId}`);
  if (!response.ok) {
    throw new Error("Network response was not ok");
  }
  return response.json();
};
